VALUES 
    (1,'Accueil Comment Faire Si','spAccueilCommentFaireSi.xsl','<?xml version=\"1.0\" encoding=\"ISO-8859-15\"?>\n<xsl:stylesheet version=\"1.0\" \n    xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\"\n    xmlns:dc=\"http://purl.org/dc/elements/1.1/\"\n    exclude-result-prefixes=\"xsl dc\">\n    \n    <xsl:output method=\"xml\" encoding=\"ISO-8859-15\" cdata-section-elements=\"script\" indent=\"yes\"/> \n    \n    <!-- Noeud de type Accueil Coment faire si -->\n    <xsl:template match=\"Publication\" mode=\"Accueil-comment-faire-si\">\n        <xsl:call-template name=\"getBarre10Themes\"/>\n        <xsl:apply-templates select=\"FilDAriane\"/>\n        <xsl:call-template name=\"getTitre\"/>\n        <div class=\"span12\" style=\"margin-left:0px;\">\n          <div class=\"well\">\n        <!-- <xsl:call-template name=\"createSommaireNoeud\" />-->\n        <xsl:apply-templates select=\"Introduction\"/>\n        <div class=\"spAccueilCommentFaireSiMain\">\n            <xsl:for-each select=\"Fiche\">\n                <xsl:if test=\"((position() mod 2) = 1) and (position() > 1)\">\n                    <br class=\"clearall\"/>\n                </xsl:if>\n                <xsl:apply-templates select=\".\" mode=\"Accueil-comment-faire-si\"/>\n            </xsl:for-each>\n            <br class=\"clearall\"/>\n        </div>\n        </div>\n        <xsl:call-template name=\"affActualite\" />       \n        <xsl:call-template name=\"affServiceEnLigne\" />      \n        <xsl:call-template name=\"affCentreDeContact\" /> \n        </div>\n    </xsl:template>\n    \n</xsl:stylesheet>\n',-1),
    (2,'Actualités','spActualite.xsl','<?xml version=\"1.0\" encoding=\"ISO-8859-15\"?>\n<xsl:stylesheet version=\"1.0\"\n    xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\" xmlns:dc=\"http://purl.org/dc/elements/1.1/\"\n    exclude-result-prefixes=\"xsl dc\">\n\n    <xsl:output method=\"xml\" encoding=\"ISO-8859-15\"\n        cdata-section-elements=\"script\" indent=\"yes\" />\n\n    <xsl:template name=\"affActualite\" mode=\"Pulication\">\n        <xsl:if test=\"count(Actualite) > 0\">\n            <div class=\"spPublicationActualite well\" id=\"sp-actualite\">\n                <h4>\n                    <xsl:text>Actualités</xsl:text>\n                </h4>\n                <xsl:apply-templates select=\"Actualite\" mode=\"Publication\" />\n            </div>\n        </xsl:if>\n    </xsl:template>\n\n    <xsl:template match=\"Actualite\" mode=\"Publication\">\n        <xsl:choose>\n            <xsl:when test=\"contains(@type,\'Fil\')\">\n                <h5>\n                    <xsl:call-template name=\"getSiteLink\">\n                        <xsl:with-param name=\"href\">\n                            <xsl:value-of select=\"@URL\" />\n                        </xsl:with-param>\n                        <xsl:with-param name=\"title\">\n                            <xsl:text>Lien vers le flux RSS d\'actualités</xsl:text>\n                        </xsl:with-param>\n                        <xsl:with-param name=\"text\">\n                            <xsl:value-of select=\"Titre\" />\n                        </xsl:with-param>\n                    </xsl:call-template>\n                </h5>\n                <xsl:call-template name=\"filActualite\" />\n            </xsl:when>\n            <xsl:otherwise>\n                <xsl:call-template name=\"articleActualite\" />\n            </xsl:otherwise>\n        </xsl:choose>\n    </xsl:template>\n\n    <xsl:template name=\"articleActualite\" mode=\"Publication\">\n        <xsl:variable name=\"title\">\n            <xsl:value-of select=\"../dc:title\" />\n            <xsl:value-of select=\"$sepFilDAriane\" />\n            <xsl:value-of select=\"Titre\" />\n        </xsl:variable>\n        <ul class=\"spPublicationActualite\">\n            <li class=\"spPublicationActualite\">\n                <h5>\n                    <xsl:call-template name=\"getSiteLink\">\n                        <xsl:with-param name=\"href\">\n                            <xsl:value-of select=\"@URL\" />\n                        </xsl:with-param>\n                        <xsl:with-param name=\"title\">\n                            <xsl:value-of select=\"$title\" />\n                        </xsl:with-param>\n                        <xsl:with-param name=\"text\">\n                            <xsl:value-of select=\"Titre\" />\n                        </xsl:with-param>\n                    </xsl:call-template>\n                </h5>\n                <xsl:text> - </xsl:text>\n                <xsl:value-of select=\"@type\" />\n            </li>\n        </ul>\n    </xsl:template>\n\n    <xsl:template name=\"filActualite\" mode=\"Publication\">\n        <ul class=\"spPublicationActualite\">\n            <xsl:choose>\n                <xsl:when test=\"item\">\n                    <xsl:for-each select=\"item\">\n                        <li class=\"spPublicationActualite\">\n                            <h4>\n                                <xsl:call-template name=\"getSiteLink\">\n                                    <xsl:with-param name=\"href\">\n                                        <xsl:value-of select=\"link\" />\n                                    </xsl:with-param>\n                                    <xsl:with-param name=\"title\">\n                                        <xsl:value-of select=\"description\" />\n                                    </xsl:with-param>\n                                    <xsl:with-param name=\"text\">\n                                        <xsl:value-of select=\"title\" />\n                                    </xsl:with-param>\n                                </xsl:call-template>\n                            </h4>\n                            <xsl:text> - </xsl:text>\n                            <xsl:call-template name=\"transformRssDate\">\n                                <xsl:with-param name=\"date\">\n                                    <xsl:choose>\n                                        <xsl:when test=\"dc:date\">\n                                            <xsl:value-of select=\"dc:date\" />\n                                        </xsl:when>\n                                        <xsl:when test=\"pubDate\">\n                                            <xsl:value-of select=\"pubDate\" />\n                                        </xsl:when>\n                                    </xsl:choose>\n                                </xsl:with-param>\n                            </xsl:call-template>\n                        </li>\n                    </xsl:for-each>\n                </xsl:when>\n                <xsl:when test=\"Error\">\n                    <li class=\"spPublicationActualite\">\n                        <xsl:value-of select=\"Error\"/>\n                    </li>\n                </xsl:when>\n            </xsl:choose>\n        </ul>\n    </xsl:template>\n</xsl:stylesheet>\n',-1),
    (3,'Administrations','spAdministrations.xsl','<?xml version=\"1.0\" encoding=\"ISO-8859-15\"?>\n<xsl:stylesheet version=\"1.0\"\n   xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\" xmlns:dc=\"http://purl.org/dc/elements/1.1/\"\n  exclude-result-prefixes=\"xsl dc\">\n\n <xsl:import href=\"spCommon.xsl\" />\n  <xsl:import href=\"spTitre.xsl\" />\n   <xsl:import href=\"spFilDAriane.xsl\" />\n  <xsl:import href=\"spOuSAdresser.xsl\" />\n\n   <xsl:output method=\"xml\" encoding=\"ISO-8859-15\"\n       cdata-section-elements=\"script\" indent=\"yes\" />\n\n <xsl:template match=\"/\">\n        <xsl:call-template name=\"getTitre\" />\n       <xsl:call-template name=\"affLetters\" />\n     <xsl:apply-templates select=\"Publication/DossierLetter\" />\n  </xsl:template>\n\n <xsl:template name=\"affLetters\">\n        <div class=\"spLetters\">\n         <xsl:variable name=\"nb\">\n                <xsl:value-of select=\"count(//Publication/DossierLetter)\" />\n            </xsl:variable>\n           <xsl:for-each select=\"//Publication/DossierLetter\">\n             <div class=\"spLetter\">\n                  <xsl:attribute name=\"style\">\n                        <xsl:text>width:</xsl:text>\n                       <xsl:value-of select=\"100 div $nb\" />\n                       <xsl:text>%</xsl:text>\n                    </xsl:attribute>\n                  <a>\n                       <xsl:attribute name=\"title\">\n                            <xsl:text>Tous les dossiers commençant par la lettre </xsl:text>\n                          <xsl:value-of select=\"Titre\" />\n                     </xsl:attribute>\n                      <xsl:attribute name=\"href\">\n                       <xsl:value-of select=\"$REFERER\"></xsl:value-of><xsl:value-of\n                          select=\"/Publication/@ID\"></xsl:value-of><xsl:text>#</xsl:text>\n                         <xsl:call-template name=\"createDossierAzId\" />\n                      </xsl:attribute>\n                      <xsl:value-of select=\"Titre\" />\n                 </a>\n              </div>\n            </xsl:for-each>\n       </div>\n    </xsl:template>\n\n <xsl:template match=\"DossierLetter\">\n        <div class=\"spDossierLetter\">\n           <xsl:attribute name=\"id\">\n               <xsl:call-template name=\"createDossierAzId\" />\n          </xsl:attribute>\n          <h2>\n              <xsl:value-of select=\"Titre\" />\n         </h2>\n         <ul class=\"spDossierLetter\">\n                <xsl:for-each select=\"Dossier\">\n                 <xsl:apply-templates select=\".\" />\n              </xsl:for-each>\n           </ul>\n     </div>\n        <xsl:call-template name=\"ancreTop\" />\n   </xsl:template>\n\n <xsl:template match=\"Dossier\">\n      <li class=\"spDossierLetter\">\n            <h3>\n              <xsl:call-template name=\"getPublicationLink\">\n                   <xsl:with-param name=\"href\">\n                        <xsl:text>LieuLocal-</xsl:text>\n                       <xsl:value-of select=\"PivotLocal\" />\n                        <xsl:text>-LieuLocal-</xsl:text>\n                      <xsl:call-template name=\"textWithoutAccent\">\n                            <xsl:with-param name=\"string\" select=\"Titre\" />\n                       </xsl:call-template>\n                  </xsl:with-param>\n                 <xsl:with-param name=\"title\">\n                       <xsl:text>Toutes les coordonnées de : </xsl:text>\n                     <xsl:value-of select=\"Titre\" />\n                 </xsl:with-param>\n                 <xsl:with-param name=\"text\">\n                        <xsl:value-of select=\"Titre\" />\n                 </xsl:with-param>\n             </xsl:call-template>\n          </h3>\n     </li>\n </xsl:template>\n\n</xsl:stylesheet>\n',-1),
    (4,'Avertissement','spAvertissement.xsl','<?xml version=\"1.0\" encoding=\"ISO-8859-15\"?>\n<xsl:stylesheet version=\"1.0\" \n    xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\"\n    xmlns:dc=\"http://purl.org/dc/elements/1.1/\"\n    exclude-result-prefixes=\"xsl dc\">\n\n    <xsl:output method=\"xml\" encoding=\"ISO-8859-15\" cdata-section-elements=\"script\" indent=\"yes\"/> \n    \n    <xsl:template name=\"affAvertissement\" mode=\"Publication\">\n        <xsl:if test=\"count(Avertissement) > 0\">\n            <div class=\"span12\" style=\"margin-left:0px;\">\n                <div class=\"spPublicationAvertissement alert alert-success\" id=\"sp-avertissement\">\n                    <xsl:apply-templates select=\"Avertissement\" mode=\"Publication\"/>\n                </div>\n            </div>\n        </xsl:if>\n    </xsl:template>\n\n    <xsl:template match=\"Avertissement\" mode=\"Publication\">\n        <h2>\n            <xsl:value-of select=\"Titre\"/>\n            <xsl:text> - </xsl:text>\n            <xsl:call-template name=\"transformRssDate\">\n                <xsl:with-param name=\"date\">\n                    <xsl:value-of select=\"@date\"/>\n                    <xsl:text>TZ</xsl:text>\n                </xsl:with-param>\n            </xsl:call-template>\n        </h2>\n        <xsl:apply-templates select=\"Texte\"/>\n    </xsl:template>\n    \n</xsl:stylesheet>\n',-1),
    (5,'Centre de contact','spCentreDeContact.xsl','<?xml version=\"1.0\" encoding=\"ISO-8859-15\"?>\n<xsl:stylesheet version=\"1.0\" \n    xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\"\n    xmlns:dc=\"http://purl.org/dc/elements/1.1/\"\n    exclude-result-prefixes=\"xsl dc\">\n\n    <xsl:output method=\"xml\" encoding=\"ISO-8859-15\" cdata-section-elements=\"script\" indent=\"yes\"/> \n    \n    <xsl:template name=\"affCentreDeContact\" mode=\"Publication\">\n        <xsl:if test=\"count(CentreDeContact) > 0\">\n            <div class=\"spPublicationCDC well\" id=\"sp-centre-de-contact\">\n                <div class=\"navbar\">\n                <div class=\"navbar-inner\">\n                <a class=\"brand\">Centres d\'appel et  de contact</a>\n                </div>\n                </div>\n                <xsl:apply-templates select=\"CentreDeContact\" mode=\"Publication\"/>\n            </div>\n        </xsl:if>\n    </xsl:template>\n\n    <xsl:template match=\"CentreDeContact\" mode=\"Publication\">\n        <xsl:variable name=\"title\">\n            <xsl:value-of select=\"../dc:title\"/>\n            <xsl:value-of select=\"$sepFilDAriane\"/>\n            <xsl:text>Centres d\'appel et de contact</xsl:text>\n            <xsl:value-of select=\"$sepFilDAriane\"/>\n            <xsl:value-of select=\"text()\"/>\n        </xsl:variable>\n        <ul class=\"spPublicationCDC\">\n            <li class=\"spPublicationCDC\">\n                <h5>\n                    <xsl:call-template name=\"getPublicationLink\">\n                        <xsl:with-param name=\"href\"><xsl:value-of select=\"@ID\"/></xsl:with-param>\n                        <xsl:with-param name=\"title\"><xsl:value-of select=\"$title\"/></xsl:with-param>\n                        <xsl:with-param name=\"text\"><xsl:value-of select=\"text()\"/></xsl:with-param>\n                    </xsl:call-template>\n                </h5>\n            </li>\n        </ul>\n    </xsl:template>\n\n    <xsl:template match=\"ServiceComplementaire\" mode=\"Centre-de-contact\">\n        <xsl:call-template name=\"getBarre10Themes\"/>\n        <xsl:call-template name=\"getFilDArianeOfRessource\"/>\n        <xsl:call-template name=\"getTitreOfRessource\"/>\n        <div class=\"span12\" style=\"margin-left:0px;\">\n            <div class=\"well\">\n                <xsl:apply-templates select=\"Texte\" mode=\"OuSAdresser\"/>\n            </div>\n        </div>   \n     </xsl:template>\n    \n</xsl:stylesheet>\n',-1),
    (6,'Comment faire si','spCommentFaireSi.xsl','<?xml version=\"1.0\" encoding=\"ISO-8859-15\"?>\n<xsl:stylesheet version=\"1.0\" \n    xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\"\n    xmlns:dc=\"http://purl.org/dc/elements/1.1/\"\n    exclude-result-prefixes=\"xsl dc\">\n\n    <xsl:output method=\"xml\" encoding=\"ISO-8859-15\" cdata-section-elements=\"script\" indent=\"yes\"/> \n        \n    <xsl:template name=\"affCommentFaireSi\" mode=\"Publication\">\n        <xsl:if test=\"count(CommentFaireSi) > 0\">\n            <div class=\"spPublicationCFS\" id=\"sp-comment-faire-si\">\n                <h4><xsl:text>Comment faire si</xsl:text></h4>\n                <ul class=\"spPublicationCFS\">\n                    <xsl:for-each select=\"CommentFaireSi\">\n                        <xsl:apply-templates select=\".\" mode=\"Publication\"/>\n                    </xsl:for-each>\n                    <xsl:call-template name=\"lienVersAccueilCommentFaireSi\"/>\n                </ul>\n            </div>\n        </xsl:if>\n    </xsl:template>\n\n    <xsl:template match=\"CommentFaireSi\" mode=\"Publication\">\n        <xsl:variable name=\"title\">\n            <xsl:value-of select=\"../dc:title\"/>\n            <xsl:value-of select=\"$sepFilDAriane\"/>\n            <xsl:text>Comment faire si</xsl:text>\n            <xsl:value-of select=\"$sepFilDAriane\"/>\n            <xsl:value-of select=\"text()\"/>\n        </xsl:variable>\n        <li class=\"spPublicationCFS\">\n            <h5>\n                <xsl:call-template name=\"getPublicationLink\">\n                    <xsl:with-param name=\"href\"><xsl:value-of select=\"@ID\"/></xsl:with-param>\n                    <xsl:with-param name=\"title\"><xsl:value-of select=\"$title\"/></xsl:with-param>\n                    <xsl:with-param name=\"text\"><xsl:value-of select=\"text()\"/></xsl:with-param>\n                </xsl:call-template>\n            </h5>\n        </li>\n    </xsl:template>\n    \n    <xsl:template name=\"lienVersAccueilCommentFaireSi\">\n        <xsl:variable name=\"idAccueil\">\n            <xsl:choose>\n                <xsl:when test=\"$CATEGORIE = \'particuliers\'\">\n                    <xsl:text>N13042</xsl:text>\n                </xsl:when>\n                <xsl:when test=\"$CATEGORIE = \'associations\'\">\n                    <xsl:text>N31000</xsl:text>\n                </xsl:when>\n                <xsl:otherwise>\n                    <xsl:text>N23971</xsl:text>\n                </xsl:otherwise>\n            </xsl:choose>\n        </xsl:variable>\n        <xsl:variable name=\"file\">\n            <xsl:value-of select=\"$XMLURL\"/>\n            <xsl:value-of select=\"$idAccueil\" />\n            <xsl:text>.xml</xsl:text>\n        </xsl:variable>\n        <li class=\"spPublicationCFS\">\n            <h5>\n                <xsl:call-template name=\"getPublicationLink\">\n                    <xsl:with-param name=\"href\"><xsl:value-of select=\"$idAccueil\"/></xsl:with-param>\n                    <xsl:with-param name=\"title\">\n                        <xsl:call-template name=\"getDescription\">\n                            <xsl:with-param name=\"id\"><xsl:value-of select=\"$idAccueil\"/></xsl:with-param>\n                        </xsl:call-template>\n                    </xsl:with-param>\n                    <xsl:with-param name=\"text\"><xsl:text>Accueil comment faire si...</xsl:text></xsl:with-param>\n                </xsl:call-template>\n            </h5>\n        </li>\n    </xsl:template>\n\n</xsl:stylesheet>\n',-1),
    (7,'Common','spCommon.xsl','<?xml version=\"1.0\" encoding=\"ISO-8859-15\"?>\n<xsl:stylesheet version=\"1.0\"\n    xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\" xmlns:dc=\"http://purl.org/dc/elements/1.1/\"\n    exclude-result-prefixes=\"xsl dc\">\n\n    <xsl:output method=\"xml\" encoding=\"ISO-8859-15\"\n        cdata-section-elements=\"script\" indent=\"yes\" />\n    <xsl:param name=\"CATEGORIE\" />\n    <xsl:param name=\"PICTOS\" />\n    <xsl:param name=\"XMLURL\" />\n    <xsl:param name=\"REFERER\" />\n    <xsl:param name=\"IMAGES\" />\n    <xsl:param name=\"HOW_TO_ID\" />\n    <xsl:param name=\"HOW_TO_TITLE\" />\n    <xsl:param name=\"FICHEID\" />\n    <!-- Affiche la barre des 10 thèmes principaux -->\n    <xsl:template name=\"getBarre10Themes\">\n        <xsl:if test=\"$CATEGORIE = \'particuliers\'\">\n            <xsl:variable name=\"file\">\n                <xsl:value-of select=\"$XMLURL\" />\n                <xsl:text>Themes.xml</xsl:text>\n            </xsl:variable>\n            <div class=\"span12 spBarre10Themes\">\n                <xsl:for-each select=\"document($file)/Noeud/Descendance/Fils\">\n                    <xsl:variable name=\"titre\">\n                        <xsl:value-of select=\"TitreContextuel\" />\n                    </xsl:variable>\n                    <div class=\"span1 spBarre10ThemesFils\">\n                        <xsl:call-template name=\"getPublicationLink\">\n                            <xsl:with-param name=\"href\">\n                                <xsl:value-of select=\"@lien\" />\n                            </xsl:with-param>\n                            <xsl:with-param name=\"title\">\n                                <xsl:value-of select=\"$titre\" />\n                            </xsl:with-param>\n                            <xsl:with-param name=\"text\">\n                                <xsl:call-template name=\"imageOfATheme\">\n                                    <xsl:with-param name=\"id\" select=\"@lien\" />\n                                </xsl:call-template>\n                            </xsl:with-param>\n                        </xsl:call-template>\n                    </div>\n                </xsl:for-each>\n            </div>\n        </xsl:if>\n    </xsl:template>\n\n    <!-- Création du lien vers une autre publication -->\n    <xsl:template name=\"getPublicationLink\">\n        <xsl:param name=\"href\" />\n        <xsl:param name=\"title\" />\n        <xsl:param name=\"text\" />\n        <xsl:choose>\n            <xsl:when test=\"$href = \'Theme\'\">\n                <a href=\"{$REFERER}\">\n                    <xsl:attribute name=\"title\">\n                        <xsl:value-of select=\"normalize-space($title)\" />\n                    </xsl:attribute>\n                    <xsl:copy-of select=\"$text\" />\n                </a>\n            </xsl:when>\n            <xsl:when test=\"contains($href,\'LieuLocal-\')\">\n                <a href=\"{$REFERER}-{$href}\">\n                    <xsl:attribute name=\"title\">\n                        <xsl:value-of select=\"normalize-space($title)\" />\n                    </xsl:attribute>\n                    <xsl:value-of select=\"$text\" />\n                </a>\n            </xsl:when>\n            <xsl:otherwise>\n                <xsl:variable name=\"linkTitle\">\n                    <xsl:call-template name=\"getTitle\">\n                        <xsl:with-param name=\"id\" select=\"$href\" />\n                    </xsl:call-template>\n                </xsl:variable>\n                <a>\n                    <xsl:attribute name=\"title\">\n                        <xsl:value-of select=\"normalize-space($title)\" />\n                    </xsl:attribute>\n                    <xsl:attribute name=\"href\">\n                        <xsl:value-of select=\"$REFERER\" />\n                        <xsl:value-of select=\"$href\" />\n                    </xsl:attribute>\n                    <xsl:copy-of select=\"$text\" />\n                </a>\n            </xsl:otherwise>\n        </xsl:choose>\n    </xsl:template>\n\n    <xsl:template name=\"getPublicationLinkBrand\">\n        <xsl:param name=\"href\" />\n        <xsl:param name=\"title\" />\n        <xsl:param name=\"text\" />\n        <xsl:choose>\n            <xsl:when test=\"$href = \'Theme\'\">\n                <a href=\"{$REFERER}\" class=\"brand\">\n                    <xsl:attribute name=\"title\">\n                        <xsl:value-of select=\"normalize-space($title)\" />\n                    </xsl:attribute>\n                    <xsl:copy-of select=\"$text\" />\n                </a>\n            </xsl:when>\n            <xsl:when test=\"contains($href,\'LieuLocal-\')\">\n                <a href=\"{$REFERER}-{$href}\" class=\"brand\">\n                    <xsl:attribute name=\"title\">\n                        <xsl:value-of select=\"normalize-space($title)\" />\n                    </xsl:attribute>\n                    <xsl:value-of select=\"$text\" />\n                </a>\n            </xsl:when>\n            <xsl:otherwise>\n                <xsl:variable name=\"linkTitle\">\n                    <xsl:call-template name=\"getTitle\">\n                        <xsl:with-param name=\"id\" select=\"$href\" />\n                    </xsl:call-template>\n                </xsl:variable>\n                <a>\n                    <xsl:attribute name=\"title\">\n                        <xsl:value-of select=\"normalize-space($title)\" />\n                    </xsl:attribute>\n                    <xsl:attribute name=\"href\">\n                        <xsl:value-of select=\"$REFERER\" />\n                        <xsl:value-of select=\"$href\" />\n                    </xsl:attribute>\n                    <xsl:attribute name=\"class\">\n                        <xsl:text>brand</xsl:text>\n                    </xsl:attribute>\n                    <xsl:copy-of select=\"$text\" />\n                </a>\n            </xsl:otherwise>\n        </xsl:choose>\n    </xsl:template>\n\n    <!-- Création du lien vers un autre site -->\n    <xsl:template name=\"getSiteLink\">\n        <xsl:param name=\"href\" />\n        <xsl:param name=\"title\" />\n        <xsl:param name=\"text\" />\n        <a rel=\"nofollow\" class=\"spTexteLienExterne\">\n            <xsl:attribute name=\"title\">\n                <xsl:value-of select=\"normalize-space($title)\" />\n            </xsl:attribute>\n            <xsl:attribute name=\"href\">\n                <xsl:value-of select=\"normalize-space($href)\" />\n            </xsl:attribute>\n            <xsl:value-of select=\"normalize-space($text)\" />\n        </a>\n    </xsl:template>\n\n    <!-- Renvoie la description d\'une publication -->\n    <xsl:template name=\"getDescription\">\n        <xsl:param name=\"id\" />\n        <xsl:variable name=\"file\">\n            <xsl:value-of select=\"$XMLURL\" />\n            <xsl:value-of select=\"$id\" />\n            <xsl:text>.xml</xsl:text>\n        </xsl:variable>\n        <xsl:variable name=\"desc\">\n            <xsl:if test=\"file:exists(file:new($file))\" xmlns:file=\"java.io.File\">\n                <xsl:value-of select=\"normalize-space(document($file)/*/dc:description)\" />\n            </xsl:if>\n        </xsl:variable>\n        <xsl:choose>\n            <xsl:when test=\"substring($desc,string-length($desc)-1) = \'.\'\">\n                <xsl:value-of select=\"$desc\" />\n                <xsl:text>..</xsl:text>\n            </xsl:when>\n            <xsl:otherwise>\n                <xsl:value-of select=\"$desc\" />\n                <xsl:text>...</xsl:text>\n            </xsl:otherwise>\n        </xsl:choose>\n    </xsl:template>\n\n    <!-- Renvoie le titre d\'une publication -->\n    <xsl:template name=\"getTitle\">\n        <xsl:param name=\"id\" />\n        <xsl:variable name=\"file\">\n            <xsl:value-of select=\"$XMLURL\" />\n            <xsl:value-of select=\"$id\" />\n            <xsl:text>.xml</xsl:text>\n        </xsl:variable>\n        <xsl:if test=\"file:exists(file:new($file))\" xmlns:file=\"java.io.File\">\n            <xsl:value-of select=\"document($file)/*/dc:title\" />\n        </xsl:if>\n    </xsl:template>\n\n    <!-- Renvoie la date d\'une publication -->\n    <xsl:template name=\"getDate\">\n        <xsl:variable name=\"date\">\n            <xsl:value-of select=\"substring(/Publication/dc:date,10,10)\" />\n        </xsl:variable>\n        <xsl:variable name=\"year\">\n            <xsl:value-of select=\"substring($date,0,5)\" />\n        </xsl:variable>\n        <xsl:variable name=\"monthInt\">\n            <xsl:value-of select=\"substring($date,6,2)\" />\n        </xsl:variable>\n        <xsl:variable name=\"dayInt\">\n            <xsl:value-of select=\"substring($date,9,2)\" />\n        </xsl:variable>\n        <div class=\"span12\">\n            <span style=\"font-style:italic;margin-bottom:5px;\">\n                <xsl:text>Mis à jour le </xsl:text>\n                <xsl:choose>\n                    <xsl:when test=\"contains($dayInt,\'0\')\">\n                        <xsl:value-of select=\"substring($dayInt,2)\" />\n                    </xsl:when>\n                    <xsl:otherwise>\n                        <xsl:value-of select=\"$dayInt\" />\n                    </xsl:otherwise>\n                </xsl:choose>\n                <xsl:call-template name=\"frenchMonth\">\n                    <xsl:with-param name=\"month\" select=\"$monthInt\" />\n                </xsl:call-template>\n                <xsl:value-of select=\"$year\" />\n                <xsl:text> par </xsl:text>\n                <xsl:call-template name=\"lowerCase\">\n                    <xsl:with-param name=\"string\">\n                        <xsl:choose>\n                            <xsl:when test=\"/Publication/dc:contributor\">\n                                <xsl:value-of select=\"/Publication/dc:contributor\" />\n                            </xsl:when>\n                            <xsl:otherwise>\n                                <xsl:value-of select=\"/Publication/dc:creator\" />\n                            </xsl:otherwise>\n                        </xsl:choose>\n                    </xsl:with-param>\n                </xsl:call-template>\n            </span>\n        </div>\n    </xsl:template>\n\n    <!-- Transforme une date au format RSS sous forme d\'une chaîne de caractères -->\n    <xsl:template name=\"transformRssDate\">\n        <xsl:param name=\"date\" />\n        <xsl:choose>\n            <xsl:when test=\"contains($date,\'Z\')\">\n                <xsl:variable name=\"onlyDate\">\n                    <xsl:value-of select=\"substring-before($date,\'T\')\" />\n                </xsl:variable>\n                <xsl:variable name=\"year\">\n                    <xsl:value-of select=\"substring-before($onlyDate,\'-\')\" />\n                </xsl:variable>\n                <xsl:variable name=\"month\">\n                    <xsl:value-of\n                        select=\"substring-before(substring-after($onlyDate,\'-\'),\'-\')\" />\n                </xsl:variable>\n                <xsl:variable name=\"day\">\n                    <xsl:value-of select=\"substring-after(substring-after($onlyDate,\'-\'),\'-\')\" />\n                </xsl:variable>\n                <xsl:value-of select=\"$day\" />\n                <xsl:choose>\n                    <xsl:when test=\"$month =\'01\'\">\n                        <xsl:text> janvier </xsl:text>\n                    </xsl:when>\n                    <xsl:when test=\"$month =\'02\'\">\n                        <xsl:text> février </xsl:text>\n                    </xsl:when>\n                    <xsl:when test=\"$month =\'03\'\">\n                        <xsl:text> mars </xsl:text>\n                    </xsl:when>\n                    <xsl:when test=\"$month =\'04\'\">\n                        <xsl:text> avril </xsl:text>\n                    </xsl:when>\n                    <xsl:when test=\"$month =\'05\'\">\n                        <xsl:text> mai </xsl:text>\n                    </xsl:when>\n                    <xsl:when test=\"$month =\'06\'\">\n                        <xsl:text> juin </xsl:text>\n                    </xsl:when>\n                    <xsl:when test=\"$month =\'07\'\">\n                        <xsl:text> juillet </xsl:text>\n                    </xsl:when>\n                    <xsl:when test=\"$month =\'08\'\">\n                        <xsl:text> août </xsl:text>\n                    </xsl:when>\n                    <xsl:when test=\"$month =\'09\'\">\n                        <xsl:text> septembre </xsl:text>\n                    </xsl:when>\n                    <xsl:when test=\"$month =\'10\'\">\n                        <xsl:text> octobre </xsl:text>\n                    </xsl:when>\n                    <xsl:when test=\"$month =\'11\'\">\n                        <xsl:text> novembre </xsl:text>\n                    </xsl:when>\n                    <xsl:when test=\"$month =\'12\'\">\n                        <xsl:text> décembre </xsl:text>\n                    </xsl:when>\n                </xsl:choose>\n                <xsl:value-of select=\"$year\" />\n            </xsl:when>\n            <xsl:otherwise>\n                <xsl:variable name=\"onlyDate\">\n                    <xsl:value-of select=\"substring-after($date,\', \')\" />\n                </xsl:variable>\n                <xsl:variable name=\"day\">\n                    <xsl:value-of select=\"substring-before($onlyDate,\' \')\" />\n                </xsl:variable>\n                <xsl:variable name=\"month\">\n                    <xsl:value-of\n                        select=\"substring-before(substring-after($onlyDate,\' \'),\' \')\" />\n                </xsl:variable>\n                <xsl:variable name=\"year\">\n                    <xsl:value-of\n                        select=\"substring-before(substring-after(substring-after($onlyDate,\' \'),\' \'),\' \')\" />\n                </xsl:variable>\n                <xsl:value-of select=\"$day\" />\n                <xsl:choose>\n                    <xsl:when test=\"$month =\'Jan\'\">\n                        <xsl:text> janvier </xsl:text>\n                    </xsl:when>\n                    <xsl:when test=\"$month =\'Feb\'\">\n                        <xsl:text> février </xsl:text>\n                    </xsl:when>\n                    <xsl:when test=\"$month =\'Mar\'\">\n                        <xsl:text> mars </xsl:text>\n                    </xsl:when>\n                    <xsl:when test=\"$month =\'Apr\'\">\n                        <xsl:text> avril </xsl:text>\n                    </xsl:when>\n                    <xsl:when test=\"$month =\'May\'\">\n                        <xsl:text> mai </xsl:text>\n                    </xsl:when>\n                    <xsl:when test=\"$month =\'Jun\'\">\n                        <xsl:text> juin </xsl:text>\n                    </xsl:when>\n                    <xsl:when test=\"$month =\'Jul\'\">\n                        <xsl:text> juillet </xsl:text>\n                    </xsl:when>\n                    <xsl:when test=\"$month =\'Aug\'\">\n                        <xsl:text> août </xsl:text>\n                    </xsl:when>\n                    <xsl:when test=\"$month =\'Sep\'\">\n                        <xsl:text> septembre </xsl:text>\n                    </xsl:when>\n                    <xsl:when test=\"$month =\'Oct\'\">\n                        <xsl:text> octobre </xsl:text>\n                    </xsl:when>\n                    <xsl:when test=\"$month =\'Nov\'\">\n                        <xsl:text> novembre </xsl:text>\n                    </xsl:when>\n                    <xsl:when test=\"$month =\'Dec\'\">\n                        <xsl:text> décembre </xsl:text>\n                    </xsl:when>\n                </xsl:choose>\n                <xsl:value-of select=\"$year\" />\n            </xsl:otherwise>\n        </xsl:choose>\n    </xsl:template>\n\n    <!-- Renvoie l\'image assoc