oose>\n        </div>\n    </xsl:template>\n    \n    <xsl:template match=\"SousChapitre\">\n        <div class=\"spTexteSousChapitre\">\n            <xsl:choose>\n                <xsl:when test=\"@type != \'\'\">\n                    <div style=\"margin-left:0px;margin-top:5px;\">\n                       <xsl:choose>\n                            <xsl:when test=\"@type = \'note\'\">\n                                <xsl:attribute name=\"class\">\n                                    <xsl:text>spTexteSousChapitreNote alert</xsl:text>\n                                </xsl:attribute>\n                            </xsl:when>\n                            <xsl:when test=\"@type = \'savoir\'\">\n                                <xsl:attribute name=\"class\">\n                                    <xsl:text>spTexteSousChapitreNote alert</xsl:text>\n                                </xsl:attribute>\n                            </xsl:when>\n                            <xsl:when test=\"@type = \'attention\'\">\n                                <xsl:attribute name=\"class\">\n                                    <xsl:text>spTexteSousChapitreNote alert alert-error</xsl:text>\n                                </xsl:attribute>\n                            </xsl:when>\n                            <xsl:when test=\"@type = \'info\'\">\n                                <xsl:attribute name=\"class\">\n                                    <xsl:text>spTexteSousChapitreNote alert</xsl:text>\n                                </xsl:attribute>\n                            </xsl:when>\n                        </xsl:choose>\n                    \n                        <xsl:call-template name=\"affTexteType\"/>\n                        <xsl:apply-templates/>\n                    </div>\n                </xsl:when>\n                <xsl:otherwise>\n                    <xsl:apply-templates/>\n                </xsl:otherwise>\n            </xsl:choose>\n        </div>\n    </xsl:template>\n\n    <xsl:template match=\"Paragraphe\" mode=\"Definition\">\n        <xsl:apply-templates/>\n    </xsl:template>\n\n    <xsl:template match=\"Paragraphe\" mode=\"OuSAdresser\">\n        <p class=\"spTexteParagraphe\"><xsl:apply-templates/></p>\n    </xsl:template>\n\n    <xsl:template match=\"Paragraphe\">\n        <xsl:choose>\n            <xsl:when test=\"name(..) = \'Titre\'\">\n                <xsl:apply-templates/>\n            </xsl:when>\n            <xsl:otherwise>\n                 <xsl:apply-templates/>\n            </xsl:otherwise>\n        </xsl:choose>\n    </xsl:template>\n\n    <xsl:template match=\"LienExterne\">\n        <xsl:variable name=\"title\">\n            <xsl:value-of select=\"text()\"/>\n            <xsl:text> - </xsl:text>\n            <xsl:value-of select=\"@URL\"/>\n        </xsl:variable>\n        <xsl:if test=\"text()\">\n            <xsl:call-template name=\"getSiteLink\">\n                <xsl:with-param name=\"href\"><xsl:value-of select=\"@URL\"/></xsl:with-param>\n                <xsl:with-param name=\"title\"><xsl:value-of select=\"$title\"/></xsl:with-param>\n                <xsl:with-param name=\"text\"><xsl:value-of select=\"text()\"/></xsl:with-param>\n            </xsl:call-template>\n        </xsl:if>\n    </xsl:template>\n\n    <xsl:template match=\"Liste\" mode=\"OuSAdresser\">\n        <xsl:apply-templates select=\".\"/>\n    </xsl:template>\n    \n    <xsl:template match=\"Liste\">\n        <xsl:choose>\n            <xsl:when test=\"name(..) = \'Item\'\">\n                <ul class=\"spTexteListeListe\"><xsl:apply-templates/></ul>\n            </xsl:when>\n            <xsl:otherwise>\n                <ul class=\"spTexteListe\"><xsl:apply-templates/></ul>\n            </xsl:otherwise>\n        </xsl:choose>\n    </xsl:template>\n\n    <xsl:template match=\"Item\">\n        <xsl:choose>\n            <xsl:when test=\"name(../..) = \'Liste\'\">\n                <li class=\"spTexteListeListe\"><xsl:apply-templates/></li>\n            </